INSERT INTO book_detail (isbn, sold_out, free_shipping, list_price, sale_price, author_bio, book_description) VALUES ('9788907914552', 0, 0, 4900, 4900, '굿윌 철학 연구소 (지은이)에 대한 간단 소개입니다.', '영국의 생물학자인 다윈은‘신이 이 세상 만물을 창조하시고 인간을 만드셨다.’는 믿음을 깨고세상을 뒤흔들었어요.그는 비글호를 타고 세계 각지를 여행한 후‘모든 생물은 진화한다.’는 진화론을 주장했어요.생물학뿐만 아니라 인간의 정신세계까지 변화시킨 다윈의 주장은오늘날까지도 영향을 미치고...') ON DUPLICATE KEY UPDATE sold_out=VALUES(sold_out), free_shipping=VALUES(free_shipping), list_price=VALUES(list_price), sale_price=VALUES(sale_price), author_bio=VALUES(author_bio), book_description=VALUES(book_description);
