INSERT INTO book_detail (isbn, sold_out, free_shipping, list_price, sale_price, author_bio, book_description) VALUES ('9788997907199', 0, 0, 1000, 1000, '알렉산더 뒤마에 대한 간단 소개입니다.', '삼총사 (Les Trois Mousquetaires)는 알렉산더 뒤마가 쓴 소설이다. 1844년 3월부터 7월까지 신문《세기》(Le Siecle)에 연재되었다. 총사가 되기 위해 파리로 온 가스코뉴 출신의 하급 귀족 달타냥이 총사 아토스, 아라미스, 포르토스를 만나 벌이는 모험을 그리고 있다.17세기 프랑스와 영국을 배경으로...') ON DUPLICATE KEY UPDATE sold_out=VALUES(sold_out), free_shipping=VALUES(free_shipping), list_price=VALUES(list_price), sale_price=VALUES(sale_price), author_bio=VALUES(author_bio), book_description=VALUES(book_description);
