INSERT INTO book_detail (isbn, sold_out, free_shipping, list_price, sale_price, author_bio, book_description) VALUES ('9788997907236', 0, 0, 1000, 1000, '찰스 디킨스에 대한 간단 소개입니다.', '《올리버 트위스트》(Oliver Twist)는 1838년에 출판된 찰스 디킨스의 소설이다. 고아 소년이 런던 슬럼가의 소매치기 일당 손아귀에서 고생하는 이야기를 그렸다. 이 소설로 그는 25세에 일약 주목받는 작가로 떠올랐다. 이 책이 출간되고 몇 년 사이에 그는 당대 가장 인기 있고 가장 널리 읽히는 작가가...') ON DUPLICATE KEY UPDATE sold_out=VALUES(sold_out), free_shipping=VALUES(free_shipping), list_price=VALUES(list_price), sale_price=VALUES(sale_price), author_bio=VALUES(author_bio), book_description=VALUES(book_description);
