INSERT INTO book_detail (isbn, sold_out, free_shipping, list_price, sale_price, author_bio, book_description) VALUES ('9788997907229', 0, 0, 1000, 1000, '찰스 디킨스에 대한 간단 소개입니다.', '≪위대한 유산≫(1861)은 디킨스가 완성한 15권의 장편소설 중 14번째 소설이다. 디킨스가 ≪All the Year Round≫ 라는 주간 잡지에 편집장으로 있을 당시, 1860년 12월에서 1861년 8월 동안에 걸쳐 이 소설을 연재했다. 이 소설이 대중들에게 큰 인기를 얻으면서 잡지의 판매부수가 올라가기까지 했다.디킨스는...') ON DUPLICATE KEY UPDATE sold_out=VALUES(sold_out), free_shipping=VALUES(free_shipping), list_price=VALUES(list_price), sale_price=VALUES(sale_price), author_bio=VALUES(author_bio), book_description=VALUES(book_description);
