INSERT INTO book_detail (isbn, sold_out, free_shipping, list_price, sale_price, author_bio, book_description) VALUES ('9788997907342', 0, 0, 1000, 1000, '해리엇 비처 스토에 대한 간단 소개입니다.', '톰 아저씨의 오두막(Uncle Tom''s Cabin)은 미국의 사실주의 작가이자 노예제 반대자인 해리엇 비처 스토(1811년-1896년)가 1852년에 발표한 대표작이다. 이 소설의 성공 덕분에 스토우는 자신의 정치적인 목적을 달성할 수 있었다. 스토우는 노예제도에 대한 반감과 노예제도에 반대하는 행동주의를 불러일으킴으로써...') ON DUPLICATE KEY UPDATE sold_out=VALUES(sold_out), free_shipping=VALUES(free_shipping), list_price=VALUES(list_price), sale_price=VALUES(sale_price), author_bio=VALUES(author_bio), book_description=VALUES(book_description);
